CREATE TABLE "public"."resources" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "organization_id" text NOT NULL, "conversation_id" uuid NOT NULL, "created_by" text NOT NULL, "current_version_id" uuid NOT NULL, "is_archived" boolean NOT NULL DEFAULT false, "metadata" jsonb NOT NULL, "source" text NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("organization_id") REFERENCES "auth"."organization"("id") ON UPDATE restrict ON DELETE cascade, FOREIGN KEY ("conversation_id") REFERENCES "public"."chat_conversations"("id") ON UPDATE restrict ON DELETE cascade, UNIQUE ("id"));
CREATE EXTENSION IF NOT EXISTS pgcrypto;
